mapper.select:
SELECT COUNT(*)
FROM tb_code
WHERE cd_depth = #{cd_depth}

mapper.insert:
INSERT INTO tb_code (
	cd_id, cd_nm, inpt_id, inpt_dt
	, cd_ord
	, up_cd_id
	, cd_depth
	, use_yn
)
VALUES (
	#{cd_id}, #{cd_nm}, #{inpt_id}, NOW()
	, #{cd_ord}
	, #{up_cd_id}
	, #{cd_depth}
	, #{use_yn}
)

mapper.update:
UPDATE tb_code
SET updt_dt = NOW()
, updt_id = #{updt_id}

	, cd_nm = #{cd_nm}


	, cd_ord = #{cd_ord}


	, use_yn = #{use_yn}

WHERE cd_id = #{cd_id}

mapper.select:
SELECT cd_id, cd_nm, cd_ord
FROM tb_code
WHERE up_cd_id = #{up_cd_id}
AND cd_depth = #{cd_depth}
AND use_yn = 'Y'
ORDER BY cd_ord ASC

SELECT cd_id, cd_nm, cd_ord
FROM tb_code
WHERE cd_depth = #{cd_depth}

	AND up_cd_id IN 

		 #{up_cd_id}


ORDER BY cd_ord ASC
