mapper.insert:
 INSERT INTO tb_user (
 	user_id, user_pwd, user_name, user_role, email, celnum, gender, use_yn, inpt_id, inpt_dt
	)
 VALUES (
 	#{user_id}, #{user_pwd}, #{user_name}, #{user_role}, #{email}, #{celnum}, #{gender}, #{use_yn}, #{inpt_id}, NOW()
	)

mapper.select:
SELECT user_id, user_name, email, celnum, gender, use_yn
FROM tb_user
WHERE user_id = #{user_id}

mapper.update:
UPDATE tb_user
SET updt_dt = NOW()
, updt_id = #{updt_id}

	, user_pwd = #{user_pwd}

, user_name = #{user_name}
, celnum = #{celnum}
, email = #{email}
, gender = #{gender}
, use_yn = #{use_yn}
WHERE user_id = #{user_id}
